import * as React from 'react';
import {Button, FormControl, MenuItem, InputLabel, Box, Select} from '@material-ui/core';




class PolicySelect extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            requiredItem: 0,
            data: [
                {"value": 1,"label": "Key Management"},
                {"value": 2,"label": "Role Management"},
                {"value": 3,"label": "Resource Management"}
            ]
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSave = this.handleSave.bind(this);
    }
    handleChange(e) {
        this.state.requiredItem = e.target.value -1;
        this.setState({});
        // this.setState({ requiredItem: e.target.value - 1});
        console.log(this.state.requiredItem);
    }
    handleCancel () {

    }
    handleSave () {
        const item=this.state.data[this.state.requiredItem];
        console.log(item);
        this.props.savePolicy(item);
    }
    createPolicy (){

    }
    render () {
        return (
            <div margin="40px">
            <h1>Policy Assets</h1>
            <p align = "right">
                <Button variant="contained" color="primary" onClick={this.createPolicy}>Create New Policy</Button>
            </p>
            <p margin="20px">Select Policy</p>
            <select
            value={this.state.requiredItem}
            onChange={this.handleChange}
            >
            <option>Choose an option</option>
            {this.state.data.map((option) => (
              <option key={option.value} value={option.value}>{option.label}</option>
            ))}
          </select>

          <Box sx={{ minWidth: 120 }}>
          <FormControl fullWidth>
          <InputLabel id="demo-simple-select-label">Select Policy</InputLabel>
          <Select
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          value={this.state.requiredItem}
          label="Policy"
          onChange={this.handleChange}
        >
        {this.state.data.map((option) => (
          <MenuItem  value={option.value}>{option.label}</MenuItem>
        ))}
          </Select>
          </FormControl>
          </Box>

            <p align="right">
                <Button variant="outlined"  onClick={this.handleCancel}> Cancel </Button>
                <label />
                <Button variant="contained" color="primary" onClick={this.handleSave}> Save </Button>
            </p>
            </div>
        );
    }
}
export default PolicySelect;
